create database krible;

use krible;
 CREATE TABLE T_content (content_id int, title varchar(200), update_time datetime, 
body_summary varchar(300));
 CREATE TABLE T_content_company (content_id int, company_ticker varchar(20), orders int);
 CREATE TABLE T_company (company_ticker varchar(20), company_name varchar(100));
 CREATE TABLE T_content_topic (content_id int, topic_id int, orders int);
 CREATE TABLE T_topic (topic_id int, topic_name varchar(20));
 CREATE TABLE T_content_author (content_id int, author_id int, orders int);
 CREATE TABLE T_author (author_id int, author_name varchar(20), affiliation varchar(20));
 CREATE TABLE T_user (user_id int, user_name varchar(20));
 CREATE TABLE T_user_topic (user_id int, topic_id int, heart int, update_time datetime);
 CREATE TABLE T_user_company (user_id int, company_ticker varchar(20), heart int, update_time 
datetime);
 CREATE TABLE T_company_account (company_ticker varchar(20), quarters int, market_cap 
varchar(20), market_rank int, sales varchar(20), net_profit varchar(20), PER float(3, 2));

