INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (1, '악재만 반영된 주가, 이제 업사이드를 대비할 때', '2023-10-30 07:56:00', '3Q23 영업이익 77억원(-71%, YoY), 어닝 쇼크 기록. 4Q23부터 중국 단체관광객 유입 본격화에 따른 수혜 기대');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (2, '3Q23 Review: 2024년에는 스마트 팩토리 스토리', '2023-10-30 07:59:00', '3분기 영업이익, 컨센서스 10% 하회. 4분기 실적 전망');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (3, '자동차/2차전지 Weekly', '2023-10-30 08:01:25', '10월 넷째주 현대차, 기아, 현대모비스 주가는 각각 -4.3%, +0.9%, -2.4%를 기록해 -3.0%를 기록한 코스피 대비 완성차는 부진한 흐름 기록.');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (4, '중국인 단체 관광객 맞을 채비에 분주', '2023-10-30 08:10:30', '3Q23 매출액 1.01조원 (-26% y-y; 이하 y-y), 영업이익 77억원 (-71%) 달성하며, 영업이익이 시장 컨센서스를 89% 하회.');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (5, '소외 속에서 고객 확대 중', '2023-10-30 08:15:09', '3Q23 OP 6,902억원(+20% YoY, +4% QoQ)으로 기대치 부합. 전동화/전장 기반의 기초 근력 확보 중.');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (6, '롤러코스터 업황에도 수익성 방어', '2023-10-30 08:30:05', '3분기 실적은 고객사의 재고 조정과 IT 제품 수요 부진으로 컨센서스 큰 폭으로 하회.');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (7, 'I am 기대치 상회에요', '2023-10-30 08:45:20', '3Q23 영업이익은 962억원(+4046% yoy, OPM 9.8%)으로 컨센서스 3.3% 상회.');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (8, '4분기에 강한 은행주', '2023-10-30 08:46:35', '고배당 기대감 지속. 다만 단기 주가 상승 부담은 존재.');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (9, '실적으로 달성한 시가총액 8위', '2023-10-30 08:50:11', '3Q23 Review: 매출비중 북미 42.8%, 유럽 24.3%.');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (10, '삼성전자 메모리 가동률 반등 예상', '2023-10-30 08:51:30', '3Q23 영업이익 329억원, 기대치 부합 예상. 2024년 상반기 실적 회복 전망.');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (11, '대장의 저력을 보여줄 때', '2023-10-25 08:01:30', '견고한 3분기 잠정실적, 주인공이 반도체가 아닌 점은 아쉬운 부분.');
INSERT INTO T_content (content_id, title, update_time, body_summary) VALUES (12, '현대차-삼성SDI 동맹: 질주를 준비하며', '2023-10-24 09:12:30', '삼성SDI는 현대자동차와 각각 손을 잡으며 전기차 사업에 힘을 쏟고 있다.');
